void emiCmdOut8(u8 cmd)
{
    emi_enable_clk();
    *((volatile unsigned char *)(HI_EMIMEM_REGBASE_ADDR + EMI_BUF_WRT_CMD)) = cmd;
    emi_disable_clk();
}
void emiDataOut8(u8 data)
{
    emi_enable_clk();
    *((volatile unsigned char *)(HI_EMIMEM_REGBASE_ADDR + EMI_WRT_DATA_8BIT)) = data;
    emi_disable_clk();
}

int emi_init(void)
{
    /*打开时钟*/
    emi_enable_clk();
    /*复位EMI*/
    hi_syscrg_emi_soft_reset_en();
    hi_syscrg_emi_soft_reset_dis();

    /*设置为normal模式*/
    set_hi_sc_ctrl32_ebi_normal_mode(1);

    /* 数据信号*/
    /* 不用设置*/

    /* 设置片选信号低有效*/
    set_hi_emi_smbcr0_cspol(0);

    /* 设置时序 */
    set_hi_sc_ctrl3_emi_sel(0);
    set_hi_emi_smbcr0_mw(0);
    set_hi_emi_smbcr0_addr_cnt(0);

    /* 设置读操作时序*/
    set_hi_emi_smbwst1r0_wst1(24);/* 总线读访问等待时间*/
    set_hi_emi_smbwst2r0_wst3(1); /* 两次读写访问之间间隔*/
    set_hi_emi_smbwstoenr0_wstoen(2);/* CS有效到读信号有效延迟*/
    set_hi_emi_smbwstoenr0_oendly(2); /* 读信号无效到片选无效时间延迟*/

        /* 设置写操作时序*/
    set_hi_emi_smbwst2r0_wst2(3); /* 总线写访问等待时间*/
    set_hi_emi_smbwst2r0_wst3(1); /* 两次读写访问之间间隔*/
    set_hi_emi_smbwstwenr0_wstwen(1); /* CS有效到写信号有效延迟*/
    set_hi_emi_smbwstwenr0_wendly(0); /* 写信号无效到片选无效时间延迟*/

    /*关闭时钟*/
    emi_disable_clk();

    return LCD_OK;
}

void lcd_reset(void)
{
    set_hi_sc_ctrl3_lcd_rst_n(1);
    mdelay(10);
    set_hi_sc_ctrl3_lcd_rst_n(0);
    mdelay(10);
    set_hi_sc_ctrl3_lcd_rst_n(1);
    mdelay(50);
}


static s32 balong_lcd_toggle(struct balong_lcd_seq *seq, int sz)
{
    u8 buf[1];
    int count;
    for (count = 0; count < sz; count++)
    {
        buf[0] = seq[count].value;
        if(seq[count].format == 'c')
        {
            emiCmdOut8(buf[0]);  /* command */

        }
        else if(seq[count].format == 'd')
        {
            emiDataOut8(buf[0]); /* data */
        }
        else if(seq[count].format=='w')
        {
            mdelay((seq[count].value)); /* delay */
        }
    }
    return LCD_OK;
}


